REPLACE INTO `ability` (`AbilityID`, `KeyName`, `Name`, `InternalID`, `Description`, `IconID`, `Implementation`, `LastTimeRowUpdated`) VALUES
	(1, 'NF_AugmentedStrength', 'Augmented Strength', 129, 'Increases Strength by the listed amount per level.', 0, 'Core.GS.RealmAbilities.NfRaStrengthEnhancer', '2023-10-24 00:00:00'),
	(2, 'Stag', 'Stag %n', 130, 'Whenever this ability is used, the Hero will shapeshift into a fearsome stag-headed Huntsman from Celtic Lore, and will receive bonus hit points. There are four different levels of of this ability: Initiate(15th level), Member(25th level), Leader(35th level), and Master(45th level). While in this form, the hero has increased hit points - +20% for the 15th level ability up to +50% for the 45th level ability. The ability lasts for thirty seconds - at the end of this time, the hero\'s maximum hits will return to normal, but he keeps any hit point gain from the ability in his current hits.', 480, '', '2000-01-01 00:00:00'),
	(3, 'Weaponry: Blunt', 'Weaponry: Blunt', 32, 'The ability to use one-handed blunt weapons of all kinds. Training in the Blunt Skill results in a decreased damage variance and gives you access to the combat styles.', 0, '', '2000-01-01 00:00:00'),
	(4, 'Weaponry: Spears', 'Weaponry: Spears', 25, 'The ability to use spears of all kinds. Training in the Spears Skill results in a decreased damage variance and gives you access to the combat styles.', 0, '', '2000-01-01 00:00:00'),
	(5, 'Weaponry: Instruments', 'Weaponry: Instruments', 133, 'The ability to wield instruments to use for various abilities.', 0, '', '2000-01-01 00:00:00'),
	(6, 'Weaponry: Staves', 'Weaponry: Staves', 13, 'The ability to use staves of all kinds except quarterstaves and mauler staves, in all realms. There is no Staves Skill. Every class has this ability.', 0, '', '2000-01-01 00:00:00'),
	(7, 'NF_AugmentedDexterity', 'Augmented Dexterity', 135, 'Increases Dexterity by the listed amount per level.', 0, 'Core.GS.RealmAbilities.NfRaDexterityEnhancer', '2023-10-24 00:00:00'),
	(8, 'NF_AvoidanceOfMagic', 'Avoidance of Magic', 136, 'Reduces all magic damage taken by the listed percentage. (This only works on damage. Does not work on disease, dots, or debuffs and does not affect the duration of crowd control spells)', 0, 'Core.GS.RealmAbilities.NfRaAvoidanceOfMagicAbility', '2023-10-24 00:00:00'),
	(9, 'Studded Armor', 'Studded Armor', 1, 'Each class has access to at least one armor type. The different armors perform differently in terms of protection from and defense against enemy damage. Some classes have access to many different types of armor.', 0, '', '2000-01-01 00:00:00'),
	(10, 'HibArmor', '0|GenericArmor;1|Armor Ability: Cloth;2|Armor Ability: Leather;3|Armor Ability: Reinforced;4|Armor Ability: Scale', 1, 'Each class has access to at least one armor type. The different armors perform differently in terms of protection from and defense against enemy damage. Some classes have access to many different types of armor.', 0, '', '2000-01-01 00:00:00'),
	(11, 'Safe Fall', 'Safe Fall', 43, 'A player may drop from greater heights, taking less damage as this skill increases.', 0, '', '2000-01-01 00:00:00'),
	(12, 'NF_Lifter', 'Lifter', 140, 'Increases the max encumbrance of the character and the speed at which the character can move rams they control by the listed percentage.', 0, 'Core.GS.RealmAbilities.NfRaLifterAbility', '2023-10-24 00:00:00'),
	(13, 'NF_VeilRecovery', 'Veil Recovery', 141, 'Reduces the duration of resurrection illnesses by the percentage listed.', 0, 'Core.GS.RealmAbilities.NfRaVeilRecoveryAbility', '2023-10-24 00:00:00'),
	(14, 'Critical Shot', 'Critical Shot %n Ability', 142, 'Must be fired from stealth, very high damage, average range and average draw time. Target get 10sec 50% resistance against critical or power shots. Will bypass Bladeturn, but not break it; as well as bypassing, Intercept, Guard and Block.', 413, '', '2022-02-02 20:34:49'),
	(15, 'NF_MasteryOfBlocking', 'Mastery of Blocking', 143, 'Increases chance to block by the listed percentage.', 0, 'Core.GS.RealmAbilities.NfRaMasteryOfBlockingAbility', '2023-10-24 00:00:00'),
	(16, 'NF_MasteryOfParrying', 'Mastery of Parrying', 144, 'Increases chance to parry by the listed percentage.', 0, 'Core.GS.RealmAbilities.NfRaMasteryOfParryingAbility', '2023-10-24 00:00:00'),
	(17, 'NF_SputinsLegacy', 'Sputin\'s Legacy', 145, 'Healer can insta-cast a resurrect buff on themselves. Buff lasts 30 seconds. If the healer dies while buff is up, they have the option to /resurrect themselves anytime within 10 minutes after death with 10% H/E/P. The healer must wait 10 seconds before /resurrecting themselves.', 3069, 'Core.GS.RealmAbilities.NfRaSputinsLegacyAbility', '2023-10-24 00:00:00'),
	(18, 'NF_MasteryOfPain', 'Mastery of Pain', 146, 'Increases chance to deal a critical hit in melee per listed percentage. (Passes on to Necro Pets)', 0, 'Core.GS.RealmAbilities.NfRaMasteryOfPainAbility', '2023-10-24 00:00:00'),
	(19, 'NF_WildPower', 'Wild Power', 147, 'Increases your spell damage by increasing your spellâ€™s chance to do critical damage by 5% chance per level of this ability. Spells affected are direct damage and bolts.', 0, 'Core.GS.RealmAbilities.NfRaWildPowerAbility', '2023-10-24 00:00:00'),
	(20, 'NF_WildHealing', 'Wild Healing', 148, 'Adds the listed percentage chance to critical heal on each target of a heal spell.', 0, 'Core.GS.RealmAbilities.NfRaWildHealingAbility', '2023-10-24 00:00:00'),
	(21, 'NF_WildMinion', 'Wild Minion', 149, 'Increases chance of pet dealing a critical hit in melee by the listed percentage.', 0, 'Core.GS.RealmAbilities.NfRaWildMinionAbility', '2023-10-24 00:00:00'),
	(22, 'NF_DesperateBowman', 'Desperate Bowman', 150, 'This ability is used with a bow. Does 300 damage and a 5 second (non resistible) stun. Bow and melee cannot be used for 15 seconds afterwards.', 3060, 'Core.GS.RealmAbilities.NfRaDesperateBowmanAbility', '2023-10-24 00:00:00'),
	(23, 'NF_MasteryOfStealth', 'Mastery of Stealth', 151, 'Modifies stealth detection and stealth movement. Camouflage counters the Mastery of Stealth bonus, allowing an archer to only be seen at the normal range. Doesn\'t affect detect hidden classes when they are detecting other detect hidden classes. (Meaning it has no affect on assassins detecting assassins.)', 0, 'Core.GS.RealmAbilities.NfRaMasteryOfStealthAbility', '2023-10-24 00:00:00'),
	(29, 'Weaponry: Slashing', 'Weaponry: Slashing', 11, 'The ability to use one-handed slashing weapons of all kinds. Training in the Slashing Skill results in a decreased damage variance and gives you access to the combat styles.', 0, '', '2000-01-01 00:00:00'),
	(30, 'NF_AugmentedConstitution', 'Augmented Constitution', 158, 'Increases Constitution by the listed amount per level.', 0, 'Core.GS.RealmAbilities.NfRaConstitutionEnhancer', '2023-10-24 00:00:00'),
	(31, 'NF_MasteryOfHealing', 'Mastery of Healing', 159, 'Increases the effectiveness of healing spells by the listed percentage.', 0, 'Core.GS.RealmAbilities.NfRaMasteryOfHealingAbility', '2023-10-24 00:00:00'),
	(32, 'Scale Armor', 'Scale Armor', 1, 'Each class has access to at least one armor type. The different armors perform differently in terms of protection from and defense against enemy damage. Some classes have access to many different types of armor.', 0, '', '2000-01-01 00:00:00'),
	(33, 'NF_MasteryOfMagery', 'Mastery of Magery', 161, 'Additional effectiveness of magical damage by listed percentage.', 0, 'Core.GS.RealmAbilities.NfRaMasteryOfMageryAbility', '2023-10-24 00:00:00'),
	(34, 'NF_FalconsEye', 'Falcon\'s Eye', 162, 'Increases the chance of dealing a critical hit with archery by the listed percentage amount.', 0, 'Core.GS.RealmAbilities.NfRaFalconsEyeAbility', '2023-10-24 00:00:00'),
	(35, 'NF_Determination', 'Determination', 163, 'Reduces the duration of all crowd control spells by the listed percentage. Effect is cumulative at each level increase.', 0, 'Core.GS.RealmAbilities.NfRaDeterminationAbility', '2023-10-24 00:00:00'),
	(36, 'NF_MasteryOfFocus', 'Mastery of Focus', 164, 'Increases the level of all spells cast by the listed amount for out-right resistance purposes. (caps at level 50)', 0, 'Core.GS.RealmAbilities.NfRaMasteryOfFocusAbility', '2023-10-24 00:00:00'),
	(37, 'NF_ArrowSummoning', 'Arrow Summoning', 165, 'Summons 30 mystical footed flight broadhead arrows: 10 barbed, 10 keen, 10 blunt into the user\'s inventory. The arrows are not tradable and will disappear when a character logs out of the game.', 3083, 'Core.GS.RealmAbilities.NfRaArrowSummoningAbility', '2023-10-24 00:00:00'),
	(38, 'Weaponry: Fist Wraps', 'Weaponry: Fist Wraps', 67, 'The ability to use Fist Wraps, a Mauler-specific weapon type. Fist Wraps function much like dual wield, but do not require a second skill for damage type (Slashing, Piercing, etc).', 0, '', '2000-01-01 00:00:00'),
	(39, 'NF_FirstAid', 'First Aid', 167, 'Heals the user by the listed percentage.', 3000, 'Core.GS.RealmAbilities.NfRaFirstAidAbility', '2023-10-24 00:00:00'),
	(40, 'NF_AdrenalineRush', 'Adrenaline Rush', 168, 'Doubles the base melee damage for 20 seconds.', 3001, 'Core.GS.RealmAbilities.NfRaAdrenalineRushAbility', '2023-10-24 00:00:00'),
	(41, 'NF_SecondWind', 'Second Wind', 169, 'Restores 100% of the user\'s endurance.', 3002, 'Core.GS.RealmAbilities.NfRaSecondWindAbility', '2023-10-24 00:00:00'),
	(42, 'NF_IgnorePain', 'Ignore Pain', 170, 'Heal that grants health equal to the percentage listed. Can be used when in combat.', 3003, 'Core.GS.RealmAbilities.NfRaIgnorePainAbility', '2023-10-24 00:00:00'),
	(43, 'Weaponry: Blades', 'Weaponry: Blades', 31, 'The ability to use one-handed bladed weapons of all kinds. Training in the Blade Skill results in a decreased damage variance and gives you access to the combat styles.', 0, '', '2000-01-01 00:00:00'),
	(44, 'Distraction', 'Distraction Ability', 40, 'Momentarily distracts the enemy, keeping them from noticing something you may be doing. ', 411, '', '2000-01-01 00:00:00'),
	(45, 'Guard', 'Guard %n', 8, 'To Guard is to assign oneself to protect another character and block incoming enemy blows to that person.', 412, '', '2000-01-01 00:00:00'),
	(46, 'NF_AugmentedQuickness', 'Augmented Quickness', 174, 'Increases Quickness by the listed amount per level.', 0, 'Core.GS.RealmAbilities.NfRaQuicknessEnhancer', '2023-10-24 00:00:00'),
	(47, 'NF_Trueshot', 'Trueshot %n', 175, 'Grants a 50% range bonus to the next arrow fired. This arrow will penetrate and pop blade turn.', 3004, 'Core.GS.RealmAbilities.NfRaTrueshotAbility', '2023-10-24 00:00:00'),
	(48, 'NF_Concentration', 'Concentration', 176, 'Refreshes the timer on quick-cast allowing for a second quick-casted spell without the normal 30 second wait.', 3005, 'Core.GS.RealmAbilities.NfRaConcentrationAbility', '2023-10-24 00:00:00'),
	(49, 'QuickCast', 'Quickcast', 3, 'Allows spell caster to cast a single spell at twice the power cost to make it so that it cannot be interrupted by melee or spells. The only exception to this is the Necromancer form of Quickcast (Facilitate Painworking). It allows the Necromancer\'s summoned undead pet to have six seconds of uninterrupted casting time.', 400, '', '2000-01-01 00:00:00'),
	(50, 'NF_MasteryOfConcentration', 'Mastery of Concentration', 178, 'Grants a 100% bonus to avoid being interrupted by any form of attack when casting a spell. The effect of the spell cast will be reduced to the percentages listed. (Necro version transfers to the pet)', 3006, 'Core.GS.RealmAbilities.NfRaMasteryOfConcentrationAbility', '2023-10-24 00:00:00'),
	(51, 'Detect Hidden', 'Detect Hidden', 42, 'Ability to detect hidden players at a range greater than the average charater.', 0, '', '2000-01-01 00:00:00'),
	(52, 'Volley', 'Volley', 180, 'A Ground Targeted Area Effect archery shot, that will hit up to a maximum number of targets 3 times. The maximum number of targets is 2 more than the number of the shot. IE: Volley 1 will hit three targets max, and Volley 8 will hit 10 targets max.', 437, '', '2000-01-01 00:00:00'),
	(53, 'NF_TheEmptyMind', 'The Empty Mind', 181, 'Grants the user 45 seconds of increased resistances to all magical damage by the percentage listed. This only works on damage and does not affect the duration of crowd control spell. (Necro version transfers to pet)', 3007, 'Core.GS.RealmAbilities.NfRaTheEmptyMindAbility', '2023-10-24 00:00:00'),
	(54, 'Plate Armor', 'Plate Armor', 1, 'Each class has access to at least one armor type. The different armors perform differently in terms of protection from and defense against enemy damage. Some classes have access to many different types of armor.', 0, '', '2000-01-01 00:00:00'),
	(55, 'Protect', 'Protect', 7, 'An ability useable on a member of your group. This allows you to reduce some aggression generated by that player.', 411, '', '2000-01-01 00:00:00'),
	(56, 'NF_MysticCrystalLore', 'Mystic Crystal Lore', 184, 'Grants a refresh of power based on the percentages listed. Cannot be used when in combat.', 3008, 'Core.GS.RealmAbilities.NfRaMysticCrystalLoreAbility', '2023-10-24 00:00:00'),
	(57, 'Weaponry: Large Weapons', 'Weaponry: Large Weapons', 34, 'The ability to use two-handed weapons of all kinds. Training in the Large Weapons Skill results in a decreased damage variance and gives you access to the combat styles.', 0, '', '2000-01-01 00:00:00'),
	(58, 'Climb Walls', 'Climb Walls', 45, 'Climbing points are available on keeps in certain locations. Having the Climbing ability means that player is capable of using the climb points to reach the inner courtyard of the keep.', 0, '', '2000-01-01 00:00:00'),
	(59, 'NF_RagingPower', 'Raging Power', 187, 'Grants a refresh of power based on the percentages listed. Can be used when in combat.', 3009, 'Core.GS.RealmAbilities.NfRaRagingPowerAbility', '2023-10-24 00:00:00'),
	(60, 'NF_Purge', 'Purge', 188, 'Removes all negative effects but leaves any applicable immunity timers in place. Level 1 purge is on a 15 minute timer but has a 5 second delay. Level 2 is on the same timer but has no delay. Level 3 is on a 5 minute timer and has no delay.', 3010, 'Core.GS.RealmAbilities.NfRaPurgeAbility', '2023-10-24 00:00:00'),
	(61, 'NF_ReflexAttack', 'Reflex Attack', 189, 'Gives a chance to automatically counter-attack with an unstyled swing (or a swing from each hand in the case of duel wielding classes) anytime a hit is taken. Works against attacks from all 360 degrees with the chance based on the percentages listed.', 3011, 'Core.GS.RealmAbilities.NfRaReflexAttackAbility', '2023-10-24 00:00:00'),
	(62, 'NF_Viper', 'Viper', 190, 'Increases the damage of poisons by the listed amount.', 3012, 'Core.GS.RealmAbilities.NfRaViperAbility', '2023-10-24 00:00:00'),
	(63, 'NF_SoldiersBarricade', 'Soldier\'s Barricade', 191, 'Grants the group an absorption bonus to all forms of damage based on the percentages listed. 30 Second duration. (Does not stack with Barrier of Fortitude or Bedazzling Aura)', 3014, 'Core.GS.RealmAbilities.NfRaSoldiersBarricadeAbility', '2023-10-24 00:00:00'),
	(64, 'Triple Wield', 'Triple Wield', 192, 'Unique to Blademasters, this is a magical third blade that can be summoned to do extra damage each attack for a short period of time.', 475, '', '2000-01-01 00:00:00'),
	(65, 'NF_ValhallasBlessing', 'Valhalla\'s Blessing', 193, 'Each spell and style used by group members has a chance of not costing power or endurance for 30 seconds.', 3086, 'Core.GS.RealmAbilities.NfRaValhallasBlessingAbility', '2023-10-24 00:00:00'),
	(66, 'NF_AugmentedAcuity', 'Augmented Acuity', 194, 'Increases primary casting stat by the listed amount per level.', 0, 'Core.GS.RealmAbilities.NfRaAcuityEnhancer', '2023-10-24 00:00:00'),
	(67, 'NF_BarrierOfFortitude', 'Barrier of Fortitude', 195, 'Grants the group a melee absorption bonus based on the percentage listed. 30 second duration. (Does not stack with Soldier\'s Barricade or Bedazzling Aura.)', 3015, 'Core.GS.RealmAbilities.NfRaBarrierOfFortitudeAbility', '2023-10-24 00:00:00'),
	(68, 'NF_VehementRenewal', 'Vehement Renewal', 196, 'Instantly heals all group members (except the user) within 2000 range for the amount listed.', 3016, 'Core.GS.RealmAbilities.NfRaVehementRenewalAbility', '2023-10-24 00:00:00'),
	(69, 'NF_PerfectRecovery', 'Perfect Recovery', 197, 'Instantly resurrects the target with no res effects with the listed amount of health and power.', 3018, 'Core.GS.RealmAbilities.NfRaPerfectRecoveryAbility', '2023-10-24 00:00:00'),
	(70, 'Weaponry: Mauler Staff', 'Weaponry: Mauler Staff', 68, 'The ability to use Mauler Staves, a type of staff specific to the Mauler class.', 0, '', '2000-01-01 00:00:00'),
	(71, 'NF_Vanish', 'Vanish', 199, 'Provides the stealther with super stealth, which can not be broken. Also will purge DoTs and Bleeds and provides immunity to crowd control. This ability lasts for 1 to 5 seconds depending on level of Vanish. The stealther also receives an increase in movement speed as listed. A stealther can not attack for 30 seconds after using this ability.', 3019, 'Core.GS.RealmAbilities.NfRaVanishAbility', '2023-10-24 00:00:00'),
	(72, 'Penetrating Arrow', 'Penetrating Arrow %n', 200, 'Chance to penetrate bladeturn at 30 Archery for 50% damage.  40 Archery for 75% damage. 50 Archery for 100% damage.', 0, '', '2000-01-01 00:00:00'),
	(73, 'Master of the Hunt', 'Master of the Hunt', 46, 'This level of Spirit of the Hunt grants a 50% hitpoint bonus.', 0, '', '2000-01-01 00:00:00'),
	(74, 'NF_SpeedOfSound', 'Speed of Sound', 202, 'Group ability that allows unstoppable speed 4 movement for the listed duration. Gives immunity to stun/snare/root and mesmerize spells. If the spell has a damage component, the target will take damage but be immune to the stun/snare/root or mesmerize. It breaks with any action taken except healing. (Will be negated by speedwarp.)', 3020, 'Core.GS.RealmAbilities.NfRaSpeedOfSoundAbility', '2023-10-24 00:00:00'),
	(75, 'NF_AmelioratingMelodies', 'Ameliorating Melodies', 203, 'Heals all members of the group (except the user) by the listed amount each tick for 30 seconds. (10 total ticks)', 3021, 'Core.GS.RealmAbilities.NfRaAmelioratingMelodiesAbility', '2023-10-24 00:00:00'),
	(76, 'Dirty Tricks', 'Dirty Tricks', 204, 'The player will toss dirt into the face of the enemy, which causes his or her fumble rate to increase temporarily. It can be used once every seven minutes.', 478, '', '2000-01-01 00:00:00'),
	(77, 'NF_AngerOfTheGods', 'Anger of the Gods', 205, '30 second group damage add that stacks with all other damage adds & ignores caps. DPS bonus as listed.', 3022, 'Core.GS.RealmAbilities.NfRaAngerOfTheGodsAbility', '2023-10-24 00:00:00'),
	(78, 'NF_WrathOfChampions', 'Wrath of Champions', 206, 'Spirit Based instantly cast PBAE with 150 radius that does the listed damage.', 3023, 'Core.GS.RealmAbilities.NfRaWrathOfChampionsAbility', '2023-10-24 00:00:00'),
	(79, 'Weaponry: Thrusting', 'Weaponry: Thrusting', 12, 'The ability to use one-handed thrusting weapons of all kinds. Training in the Thrusting Skill results in a decreased damage variance and gives you access to the combat styles.', 0, '', '2000-01-01 00:00:00'),
	(80, 'NF_VolcanicPillar', 'Volcanic Pillar', 208, 'Heat based AE damage spell with 500 radius. Damage as listed. 2 second non-interruptible cast time. 1500 range Target is enemy.', 3024, 'Core.GS.RealmAbilities.NfRaVolcanicPillarAbility', '2023-10-24 00:00:00'),
	(81, 'NF_DecimationTrap', 'Decimation Trap', 209, 'AE damage trap with 350 radius. Damage as listed. The trap lasts ten minutes or until detonated (whichever comes first). Energy based. 2 second non-interruptible cast time', 3025, 'Core.GS.RealmAbilities.NfRaDecimationTrapAbility', '2023-10-24 00:00:00'),
	(82, 'Shield Ability: Large Shields', 'Shield Ability: Large Shields', 2, 'An independent line of defense, which can be raised through training to gain special shield attacks.  The larger the shield, the more protection it provides', 0, '', '2000-01-01 00:00:00'),
	(83, 'Weaponry: Thrown', 'Weaponry: Thrown', 27, 'The ability to use thrown knives. This ability is unspeccable and unique to Shadowblades.', 0, '', '2000-01-01 00:00:00'),
	(84, 'NF_Tireless', 'Tireless', 212, 'This gives a bonus of one endurance regeneration per tick. This does not stack with any other form of endurance regeneration.', 0, 'Core.GS.RealmAbilities.NfRaEndRegenEnhancer', '2000-01-01 00:00:00'),
	(85, 'NF_LongWind', 'Long Wind', 213, 'Decreases the amount of endurance taken per tick when sprinting, by the number listed.', 0, 'Core.GS.RealmAbilities.NfRaLongWindAbility', '2023-10-24 00:00:00'),
	(86, 'NF_NegativeMaelstrom', 'Negative Maelstrom', 214, 'A 2 second non-interruptible cast time cold based pulsing GTAE storm with 350 radius. Ground target range is 1500. The damage of the storm grows with each pulse.', 3026, 'Core.GS.RealmAbilities.NfRaNegativeMaelstromAbility', '2023-10-24 00:00:00'),
	(87, 'NF_ThornweedField', 'Thornweed Field', 215, 'Creates a field of thorns that damage and snare all enemies caught within. 500 radius. Essence Damage. Pulses every 3 seconds. 2 second non-interruptible cast time. 1500 range', 3027, 'Core.GS.RealmAbilities.NfRaThornweedFieldAbility', '2023-10-24 00:00:00'),
	(88, 'Weaponry: Flexible', 'Weaponry: Flexible', 50, 'The ability to use one-handed flexible weapons of all kinds, such as flails. Training in the Flexible Weapons Skill results in a decreased damage variance and gives you access to the combat styles. This skill is unique to Reavers and Heretics.', 0, '', '2000-01-01 00:00:00'),
	(89, 'Weaponry: Polearms', 'Weaponry: Polearms', 15, 'The ability to use polearm weapons of all kinds. Training in the Polearm Skill results in an increased overall damage and gives you access to the combat styles.', 0, '', '2000-01-01 00:00:00'),
	(90, 'Weaponry: Scythe', 'Weaponry: Scythe', 52, 'The ability to use scythes of all kinds. Training in the Scythes Skill results in a decreased damage variance and gives you access to the scythe combat styles. This skill is unique to Valewalkers.', 0, '', '2000-01-01 00:00:00'),
	(91, 'NF_Ichor', 'Ichor of the Deep', 219, 'Spirit-based root plus direct damage spell. 1875 range with 500 radius. Damage and duration as listed. Two second non-interruptible cast time.', 3028, 'Core.GS.RealmAbilities.NfRaIchorOfTheDeepAbility', '2023-10-24 00:00:00'),
	(92, 'Danger Sense', 'Danger Sense', 41, 'Notifies the player when they and their party have been spotted by scout mobs.', 0, '', '2000-01-01 00:00:00'),
	(93, 'NF_BedazzlingAura', 'Bedazzling Aura', 221, 'Grants the group increased resistance to magical damage for 30 seconds by the percentage listed. (Does not stack with Soldier\'s Barricade or Barrier of Fortitude)', 3029, 'Core.GS.RealmAbilities.NfRaBedazzlingAuraAbility', '2023-10-24 00:00:00'),
	(94, 'NF_Juggernaut', 'Juggernaut', 222, 'Increases the effective level of the pet by the listed number of levels for 60 seconds. (capped at level 70)', 3030, 'Core.GS.RealmAbilities.NfRaJuggernautAbility', '2023-10-24 00:00:00'),
	(95, 'NF_StaticTempest', 'Static Tempest', 223, 'Delivers a 360 radius targeted storm that procs a 3 second unresistible stun every 5 seconds for the duration listed.', 3031, 'Core.GS.RealmAbilities.NfRaStaticTempestAbility', '2023-10-24 00:00:00'),
	(96, 'NF_DashingDefense', 'Dashing Defense', 224, 'The tank can block and parry for all groupmates within a 1000 radius for the duration listed. Each attack only has the chance of being blocked/parried once, regardless of how many characters in a group have Dashing Defense active at one time.', 3032, 'Core.GS.RealmAbilities.NfRaDashingDefenseAbility', '2023-10-24 00:00:00'),
	(97, 'NF_DualThreat', 'Dual Threat', 225, 'Grants a bonus chance to critical hit on both melee and magic based attacks. Percentage chance as listed. (While this stacks with Wild Power and Mastery of Pain, please note that there is a 50% hard cap on the chance to crit.)', 0, 'Core.GS.RealmAbilities.NfRaDualThreatAbility', '2023-10-24 00:00:00'),
	(98, 'NF_Charge', 'Charge', 53, 'Target does not consume endurance while sprinting for the duration.', 400, 'Core.GS.RealmAbilities.NfRaChargeAbility', '2023-10-24 00:00:00'),
	(99, 'NF_DivineIntervention', 'Divine Intervention', 227, 'Gives the group a buff that provides a pool of healing. If anyone in the group takes damage inflicted through combat, they will be immediately healed from the healing pool. Pool size is based on the numbers listed. Does not heal the user. Buff Duration: 20 minutes', 3035, 'Core.GS.RealmAbilities.NfRaDivineInterventionAbility', '2023-10-24 00:00:00'),
	(100, 'Leader of the Hunt', 'Leader of the Hunt', 46, 'This level of Spirit of the Hunt grants a 40% hitpoint bonus.', 0, '', '2000-01-01 00:00:00'),
	(101, 'Weaponry: Hammers', 'Weaponry: Hammers', 24, 'The ability to use one-handed and two-handed hammers of all kinds. Training in the Hammers Skill results in a decreased damage variance and gives you access to the combat styles.', 0, '', '2000-01-01 00:00:00'),
	(102, 'NF_NaturesWomb', 'Nature\'s Womb', 230, 'Insta-cast spell that stuns the druid for 5 seconds and converts all damage taken into healing.', 3052, 'Core.GS.RealmAbilities.NfRaNaturesWombAbility', '2023-10-24 00:00:00'),
	(103, 'NF_PhysicalDefense', 'Physical Defense', 231, 'Reduces all physical damage taken by the listed percentage.', 0, 'Core.GS.RealmAbilities.NfRaPhysicalDefenseAbility', '2023-10-24 00:00:00'),
